```cpp
class IProgress {
public:
    virtual void DoProgresss(float value) = 0;
    virtual ~IProgress() {}
}
class FileSplitter {
    string m_filePath;
    int m_fileNumber;
    List<IProgress*> m_iprogressList; //, 
public:
    FileSplitter(const strign& filePath, int fileNumber):
    	m_filePath(filePath),
    	m_fileNumber(fileNumber) {

    }

    void addIProgress(IProgress* iprogress) {
        m_iprogressList.add(iprogress);
    }

    void removeIProgress(IProgress* iprogress) {
        m_iprogressList.remove(iprogress);
    }


    void split() {
        //1. 

        //2. 
        for(int i = 0; i < m_fileNumber; i++) {
            //...

            float progressValue = m_fileNumber;
            progressValue = (i + 1) / progressValue;
            onProgress(progressValue);
        }
    }
protected:
    void onProgress(float value) {
        List<IProgress*>::iterator it = m_iprogressList.begin();

		while(it != m_iprogressList.end()) {
            (*it)->DoProgress(value);
            it++;
        }
    }
}
```

```cpp
class MainForm: public Form, public IProgress{
    TextBox* txtFilePath;
    TextBox* txtFileNumber;
    ProgressBar* progressbar;
public:
    void Button1_Click() {
        string filePath = txtFilePath->getText();
        int number = atoi(txtFileNumber->getText().c_str());

        ConsoleNotifier cn;
        FileSplitter splitter(filePath, number);

        splitter.addIprogress(this);
        splitter.addIprogress(&cn);

        splitter.split();
    }

    virtual void DoProgress(float value) {
        progressBar->setValue(value);
    }
}

class ConsoleNotifier: public IProgress {
public:
    virtual void DoProgress(float value) {
        cout << ".";
    }
}
```



